import {categoryConstants} from '../actions/constants';


const initstate = {
    categories: [],
    loading: false,
    error: null
};

export default (state = initState, action) =>{
    switch(action.type){
        case categoryConstants.GET_ALL_CATEGORIES_SUCCESS:
            state = {
                ...state,
                categories: action.payload.categories
            }
            break;
    }
    return state;
}
