import {authConstants} from "../actions/constants";

const initstate ={
    token: null,
    user: {
        firstName:'',
        lastName:'',
        email:'',
    },
    authenticate:false,
    authenticating:false
};

export default(state = initstate,action) =>{

    console.log(action);
    switch(action.type){
        case authConstants.LOGIN_REQUEST:
            state={
                ...state,
                authenticating: true
            }
            break;
    
    }
     return state;
}
